	public static long repeatedString(String s, long n) {
        
        long aInString = 0;
        for (int i = 0; i < s.length(); i++) {
            if (s.charAt(i) == 'a') {
                aInString++;
            }
        }
        
        if (aInString == 0) {
            return aInString;
        }
        
        long diff = n % (s.length());
        long counter = (aInString * (n - diff)) / s.length();
        
        for (int i = 0; i <= diff; i++) {
            if (s.charAt(i) == 'a') {
                counter++;
            }
        }
        
        return counter;
    }
	
	public static void minimumBribes(List<Integer> q) {
	    
		//Link do exerc√≠cio:
		//https://www.hackerrank.com/challenges/new-year-chaos/problem?isFullScreen=true&h_l=interview&playlist_slugs%5B%5D=interview-preparation-kit&playlist_slugs%5B%5D=arrays
		long bribe = 0;
        PriorityQueue<Integer> queue = new PriorityQueue<>();
        queue.addAll(q);
        for (int i = 0; i < q.size(); i++) {
            long indBribe = 0;
            int currentElement = q.get(i);
            List<Integer> removedNumbers = new ArrayList<>();
            
            while (!queue.isEmpty() && currentElement >= queue.peek()) {
                if (currentElement != queue.peek()) {
                    indBribe++;
                    removedNumbers.add(queue.poll());
                    
                    if (indBribe == 3) {
                        System.out.println("Too chaotic");
                        return;
                    }
                } else {
                    queue.poll();
                }
            }
            
            queue.addAll(removedNumbers);
            bribe += indBribe;
            
        }
        
        System.out.println(bribe);

    }

